import{_ as o,o as t,c as e,a3 as r}from"./chunks/framework.DyVDm1WL.js";const n=JSON.parse('{"title":"Facebook批次給指定的小組定時發帖","description":"","frontmatter":{},"headers":[],"relativePath":"zh/facebook/fb_group_post.md","filePath":"zh/facebook/fb_group_post.md"}'),s={name:"zh/facebook/fb_group_post.md"};function c(l,a,b,p,i,h){return t(),e("div",null,[...a[0]||(a[0]=[r('<h1 id="facebook批次給指定的小組定時發帖" tabindex="-1">Facebook批次給指定的小組定時發帖 <a class="header-anchor" href="#facebook批次給指定的小組定時發帖" aria-label="Permalink to &quot;Facebook批次給指定的小組定時發帖&quot;">​</a></h1><h2 id="功能背景說明" tabindex="-1">功能背景說明 <a class="header-anchor" href="#功能背景說明" aria-label="Permalink to &quot;功能背景說明&quot;">​</a></h2><p>Facebook上有一個很重要的功能就是Facebook小組，不同小組偏向不同的主題興趣點，比如特定地區二手交易小組，戶外騎行小組等等，這些小組都是個人建立的，吸納行業相關的使用者進入，在其中發帖或者群聊等。此功能可以控制大量小號定時自動在Facebook小組中發帖，支援設定匿名，定位等資訊.</p><p>很多小組成員幾萬或者是幾萬人，找到行業相關的小組，持續的自動發帖能起到持久自動曝光的作用。</p><p>發完貼的同時系統支援給帖子發一個首評，一般是將相關連結放在評論裡面.</p><h2 id="支援的執行平臺" tabindex="-1">支援的執行平臺 <a class="header-anchor" href="#支援的執行平臺" aria-label="Permalink to &quot;支援的執行平臺&quot;">​</a></h2><ul><li>指紋瀏覽器</li><li>手機</li></ul><h2 id="指紋瀏覽器功能截圖" tabindex="-1">指紋瀏覽器功能截圖 <a class="header-anchor" href="#指紋瀏覽器功能截圖" aria-label="Permalink to &quot;指紋瀏覽器功能截圖&quot;">​</a></h2><p><img src="https://spub.xrobot.tech/blog/src/img/whatscrm/fb/fb_group_post_01.png" alt="Facebook小組自動發帖" loading="lazy"><img src="https://spub.xrobot.tech/blog/src/img/whatscrm/fb/fb_group_post_02.png" alt="Facebook小組自動發帖" loading="lazy"></p><h2 id="手機功能截圖" tabindex="-1">手機功能截圖 <a class="header-anchor" href="#手機功能截圖" aria-label="Permalink to &quot;手機功能截圖&quot;">​</a></h2><p><img src="https://spub.xrobot.tech/blog/src/img/whatscrm/fb/fb_group_post_03.png" alt="Facebook小組自動發帖" loading="lazy"><img src="https://spub.xrobot.tech/blog/src/img/whatscrm/fb/fb_group_post_04.png" alt="Facebook小組自動發帖" loading="lazy"></p>',11)])])}const d=o(s,[["render",c]]);export{n as __pageData,d as default};
