import{_ as o,c as t,o as r,a2 as c,j as a,a as i}from"./chunks/framework.BfF5VrUX.js";const k=JSON.parse('{"title":"Facebook商城开发全球本地小B客户","description":"","frontmatter":{},"headers":[],"relativePath":"zh/facebook/markting_business.md","filePath":"zh/facebook/markting_business.md"}'),l={name:"zh/facebook/markting_business.md"};function s(b,e,n,h,p,u){return r(),t("div",null,[...e[0]||(e[0]=[c('<h1 id="facebook商城开发全球本地小b客户" tabindex="-1">Facebook商城开发全球本地小B客户 <a class="header-anchor" href="#facebook商城开发全球本地小b客户" aria-label="Permalink to &quot;Facebook商城开发全球本地小B客户&quot;">​</a></h1><h2 id="功能背景说明" tabindex="-1">功能背景说明 <a class="header-anchor" href="#功能背景说明" aria-label="Permalink to &quot;功能背景说明&quot;">​</a></h2><p>Facebook 坐拥30亿用户，其平台内提供的Facebook商城专门提供给全球各地本地小卖家出售产品，这些卖家中很多都是本地小商家（类似于国内的咸鱼），除中国外，绝大部分的海外国家的产品销售主要通过线下进行的，所以通过Facebook商城来寻找指定国家当地的小卖家，通过产品咨询或者私信的方式来扩展自己的销售渠道是一个可操作的方案。</p><h2 id="功能逻辑" tabindex="-1">功能逻辑 <a class="header-anchor" href="#功能逻辑" aria-label="Permalink to &quot;功能逻辑&quot;">​</a></h2><p>控制利用Facebook小号，每日根据关键词自动搜索相关产品，根据相关产品给店家发送产品咨询信息或者个人私信信息</p><h2 id="支持的运行平台" tabindex="-1">支持的运行平台 <a class="header-anchor" href="#支持的运行平台" aria-label="Permalink to &quot;支持的运行平台&quot;">​</a></h2><ul><li>指纹浏览器</li><li>手机</li></ul><h2 id="指纹浏览器功能截图" tabindex="-1">指纹浏览器功能截图 <a class="header-anchor" href="#指纹浏览器功能截图" aria-label="Permalink to &quot;指纹浏览器功能截图&quot;">​</a></h2><p><img src="https://spub.xrobot.tech/blog/src/img/whatscrm/fb/fb_marketplace_01.png" alt="Facebook商城给商家发消息" loading="lazy"><img src="https://spub.xrobot.tech/blog/src/img/whatscrm/fb/fb_marketplace_02.png" alt="Facebook商城给商家发消息" loading="lazy"></p><h2 id="手机功能截图" tabindex="-1">手机功能截图 <a class="header-anchor" href="#手机功能截图" aria-label="Permalink to &quot;手机功能截图&quot;">​</a></h2><p><img src="https://spub.xrobot.tech/blog/src/img/whatscrm/fb/fb_marketplace_03.png" alt="Facebook商城给商家发消息" loading="lazy"><img src="https://spub.xrobot.tech/blog/src/img/whatscrm/fb/fb_marketplace_04.png" alt="Facebook商城给商家发消息" loading="lazy"></p><h2 id="facebook-商城号说明" tabindex="-1">Facebook 商城号说明 <a class="header-anchor" href="#facebook-商城号说明" aria-label="Permalink to &quot;Facebook 商城号说明&quot;">​</a></h2><p>不是所有的Facebook商城号带商城功能，如发现有的Facebook账号本身没有商城功能，建议手机上养一段时间，有很高的概率会出现这个功能</p><p>部分账号会出现Facebook商城功能有时有，有时没有这种情况，只要账户没有被封，设置手机养号或者放几天，也有很高概率会恢复次功能</p><h2 id="指纹浏览器版本视频讲解" tabindex="-1">指纹浏览器版本视频讲解 <a class="header-anchor" href="#指纹浏览器版本视频讲解" aria-label="Permalink to &quot;指纹浏览器版本视频讲解&quot;">​</a></h2><div class="tip custom-block"><p class="custom-block-title">提示</p><p>以下为Youtube视频，请在海外环境下观看</p></div>',16),a("iframe",{width:"100%",height:"390",src:"https://www.youtube.com/embed/yNokvjY-i9o?si=QyBlNoploe0bn7cA",title:"YouTube video player",frameborder:"0",allow:"accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture; web-share",referrerpolicy:"strict-origin-when-cross-origin",allowfullscreen:"",style:{"background-color":"#E7E9EB","background-image":"url(https://spub.xrobot.tech/assets/images/youtube.svg)","background-poisition":"center","background-repeat":"no-repeat","background-size":"8%"},loading:"lazy"},null,-1),a("h2",{id:"手机版本视频讲解",tabindex:"-1"},[i("手机版本视频讲解 "),a("a",{class:"header-anchor",href:"#手机版本视频讲解","aria-label":'Permalink to "手机版本视频讲解"'},"​")],-1),a("div",{class:"tip custom-block"},[a("p",{class:"custom-block-title"},"提示"),a("p",null,"以下为Youtube视频，请在海外环境下观看")],-1),a("iframe",{width:"100%",height:"390",src:"https://www.youtube.com/embed/hPyA21TNhNw?si=Jo1R5Kd_8k15MJwa",title:"YouTube video player",frameborder:"0",allow:"accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture; web-share",referrerpolicy:"strict-origin-when-cross-origin",allowfullscreen:"",style:{"background-color":"#E7E9EB","background-image":"url(https://spub.xrobot.tech/assets/images/youtube.svg)","background-poisition":"center","background-repeat":"no-repeat","background-size":"8%"},loading:"lazy"},null,-1)])])}const m=o(l,[["render",s]])