import{_ as a,c as e,o as s,a2 as i}from"./chunks/framework.BfF5VrUX.js";const b=JSON.parse('{"title":"Shopify線上客服接入說明","description":"","frontmatter":{},"headers":[],"relativePath":"zh/fun/website_manager_shopify.md","filePath":"zh/fun/website_manager_shopify.md"}'),o={name:"zh/fun/website_manager_shopify.md"};function p(r,t,l,n,h,c){return s(),e("div",null,[...t[0]||(t[0]=[i(`<h1 id="shopify線上客服接入說明" tabindex="-1">Shopify線上客服接入說明 <a class="header-anchor" href="#shopify線上客服接入說明" aria-label="Permalink to &quot;Shopify線上客服接入說明&quot;">​</a></h1><h3 id="_1-建立獨立站客服" tabindex="-1">1.建立獨立站客服 <a class="header-anchor" href="#_1-建立獨立站客服" aria-label="Permalink to &quot;1.建立獨立站客服&quot;">​</a></h3><ul><li>進入個人中心</li><li>點選進入獨立站客服</li><li>點選建立</li><li>輸入網址域名</li><li>輸入網址標題</li><li>輸入客服對外顯示暱稱</li><li>輸入客服對外顯示簡介</li><li>選擇並設定頭像，頭像寬高比需要1:1</li><li>點選儲存</li><li>複製客服連結</li><li>拼接客服 JavaScript 程式碼</li></ul><p><img src="https://spub.xrobot.tech/assets/images/banner/im_01.png?t=1706529230" alt="帖子" loading="lazy"></p><p><img src="https://spub.xrobot.tech/assets/images/banner/im_02.png?t=1706529230" alt="帖子" loading="lazy"></p><p>複製出來的連結類似這種 <strong><a href="https://spub.xrobot.tech/chat/xchat.js?u=928814" target="_blank" rel="noreferrer">https://spub.xrobot.tech/chat/xchat.js?u=928814</a></strong></p><p><img src="https://spub.xrobot.tech/assets/images/banner/im_03.png?t=1706529230" alt="帖子" loading="lazy"></p><p>假設您的客服連結是</p><pre><code>https://spub.xrobot.tech/chat/xchat.js?u=928814
</code></pre><p>拼接出的<strong>客服 JavaScript 程式碼</strong></p><pre><code>&lt;script type=&quot;text/javascript&quot; src=&quot;https://spub.xrobot.tech/chat/xchat.js?u=928814&amp;t=1.0.0&quot;&gt;&lt;/script&gt;
</code></pre><p>其中<strong>t=1.0.0</strong>用於指定客服程式碼的版本號，後期客服系統升級改一下這裡的值就行，比如<strong>t=1.1.0</strong></p><h3 id="_2-shopify-商店整合客服連結" tabindex="-1">2.Shopify 商店整合客服連結 <a class="header-anchor" href="#_2-shopify-商店整合客服連結" aria-label="Permalink to &quot;2.Shopify 商店整合客服連結&quot;">​</a></h3><p>登入 Shopify，進入「線上商店-模板」模組</p><ul><li>點選頁面右側【...】按鈕；</li><li>選擇【編輯程式碼】；</li></ul><p><img src="https://spub.xrobot.tech/assets/images/banner/Shopify-08.png?t=1706529230" alt="帖子" loading="lazy"></p><ul><li>在【佈局】中開啟【theme.liquid】檔案，並在<code>&lt;/body&gt;</code>前貼上上 JavaScript 程式碼，點選儲存即可成功安裝。</li></ul><p><img src="https://spub.xrobot.tech/assets/images/banner/Shopify-09.png?t=1706529230" alt="帖子" loading="lazy"></p><h3 id="_3-新增坐席" tabindex="-1">3.新增坐席 <a class="header-anchor" href="#_3-新增坐席" aria-label="Permalink to &quot;3.新增坐席&quot;">​</a></h3><p>以上操作預設只會新增獨立站建立者一個坐席，想要新增多個坐席，按照以下圖示操作</p><blockquote><p>每個坐席都是伊叡行銷有效賬戶，開通坐席請聯絡客服將您的坐席賬戶設定合理有效期</p></blockquote><p><img src="https://spub.xrobot.tech/assets/images/banner/im_10.png?t=1706529230" alt="帖子" loading="lazy"></p><p><img src="https://spub.xrobot.tech/assets/images/banner/im_11.png?t=1706529230" alt="帖子" loading="lazy"></p><p><img src="https://spub.xrobot.tech/assets/images/banner/im_12.png?t=1706529230" alt="帖子" loading="lazy"></p>`,24)])])}const u=a(o,[["render",p]]);export{b as __pageData,u as default};
