import{_ as e,c as t,o as r,a2 as o}from"./chunks/framework.BfF5VrUX.js";const _=JSON.parse('{"title":"私信客服使用说明","description":"","frontmatter":{},"headers":[],"relativePath":"zh/start/im.md","filePath":"zh/start/im.md"}'),s={name:"zh/start/im.md"};function i(l,a,n,h,p,c){return r(),t("div",null,[...a[0]||(a[0]=[o('<h1 id="私信客服使用说明" tabindex="-1">私信客服使用说明 <a class="header-anchor" href="#私信客服使用说明" aria-label="Permalink to &quot;私信客服使用说明&quot;">​</a></h1><h2 id="开启设置" tabindex="-1">开启设置 <a class="header-anchor" href="#开启设置" aria-label="Permalink to &quot;开启设置&quot;">​</a></h2><ul><li>开启指纹环境的任务执行开关</li><li>开启指纹环境对应账户的自动消息读取开关</li></ul><blockquote><p>新消息读取快慢取决于消息读取间隔设置（可在客服界面修改）</p></blockquote><h2 id="注意" tabindex="-1">注意 <a class="header-anchor" href="#注意" aria-label="Permalink to &quot;注意&quot;">​</a></h2><p>开启账户的私信读取开关之后，系统会按照指定的时间间隔自动创建消息读取任务，并执行</p><p>一般不建议将消息读取间隔时间设置太小，这样会占用大量任务执行时间，导致其它任务，比如发帖，私信，分享等常规任务长时间排队执行，造成任务执行慢的现象，</p><p><strong>建议消息读取间隔最少不低于300秒</strong></p><h2 id="具体步骤" tabindex="-1">具体步骤 <a class="header-anchor" href="#具体步骤" aria-label="Permalink to &quot;具体步骤&quot;">​</a></h2><p>下面以某一个Facebook指纹环境为例，其它社交媒体操作方式完全一致</p><h4 id="_1-开启指纹环境的任务执行开关" tabindex="-1">1.开启指纹环境的任务执行开关 <a class="header-anchor" href="#_1-开启指纹环境的任务执行开关" aria-label="Permalink to &quot;1.开启指纹环境的任务执行开关&quot;">​</a></h4><p><img src="https://spub.xrobot.tech/assets/images/banner/task_open.png?t=1706529230" alt="开启指纹环境的任务执行开关" loading="lazy"></p><h4 id="_2-开启指纹环境对应账户的自动消息读取开关" tabindex="-1">2.开启指纹环境对应账户的自动消息读取开关 <a class="header-anchor" href="#_2-开启指纹环境对应账户的自动消息读取开关" aria-label="Permalink to &quot;2.开启指纹环境对应账户的自动消息读取开关&quot;">​</a></h4><p>同时你也可以设置消息读取间隔，这个会影响显示新消息的及时性</p><p><img src="https://spub.xrobot.tech/assets/images/banner/message.png?t=1706529230" alt="开启指纹环境对应账户的自动消息读取开关" loading="lazy"></p>',15)])])}const m=e(s,[["render",i]]);export{_ as __pageData,m as default};
